/* This node will receive status messages from "tcp in" node and "mqtt out".

The "tcp in" status looks like this:
status:
    text: "1 connection"
    event: "connect"
    ip: "::ffff:127.0.0.1"
    port: 57886
_session:
    type: "tcp"
    id: "4a30194f.2d0008"
source:
    id: "51c67f81.6eaad"
    type: "tcp in"
_msgid: "bb2b70c5.abf9f"

The "mqtt out" status like this:
status:
    fill: "yellow"
    shape: "ring"
    text: "node-red:common.status.connecting"
source:
    id: "9a6c9ede.e185c"
    type: "mqtt out"
_msgid: "45a3aa0f.960234"

The following code will compute the following status:
* green (connected): if both ends are connected.
* yellow ring (connecting): if any of both ends are still connecting.
* grey ring (unknown): for undefined states, especially if more than
  one endpoint is connected to "tcp in"
* red (disconnected): If one of both nodes is not connected.
*/

// Store the last status for each of the two nodes, as only one is received
// at a time but both are required to compute the status.
if (msg.status.source.type === "mqtt out") {
    context.set('mqtt_status_text', msg.status.text)
}
if (msg.status.source.type === "tcp in") {
    context.set('tcp_status_text', msg.status.text)
}

// Compute the status assume disconnected as default value.
let status
let mqtt_status_text = context.get("mqtt_status_text") || "node-red:common.status.disconnected"
let tcp_status_text = context.get("tcp_status_text") || "0 connection"

if (mqtt_status_text === "node-red:common.status.connected" && 
        tcp_status_text === "1 connection") {
    status = {
        "fill": "green",
        "shape": "dot",
        "text": "node-red:common.status.connected"
    }
    return {"status": status}
}

if (mqtt_status_text === "node-red:common.status.disconnected" || 
        tcp_status_text === "0 connection") {
    status = {
        "fill": "red",
        "shape": "ring",
        "text": "node-red:common.status.disconnected",
        "tcp status": tcp_status_text
    }
    return {"status": status}
}

// TCP in has no connecting phase. Hence only connecting status
// if already connected to tcp in.
if (mqtt_status_text === "node-red:common.status.connecting" && 
        tcp_status_text === "1 connection") {
    status = {
        "fill": "yellow",
        "shape": "ring",
        "text": "node-red:common.status.connecting"
    }
    return {"status": status}
}

// For every other state, which should 
status = {
    "fill": "grey",
    "shape": "ring",
    "text": "unknown"
}

return {"status": status}

let heartbeat_period = env.get("heartbeat_period")

//timestamps as usual in milliseconds, hearbeat period in seconds
payload = {
    "this_heartbeats_timestamp": msg.payload,
    "next_heartbeats_timestamp": msg.payload + heartbeat_period * 1000
}
return {"payload": payload}

// from https://stackoverflow.com/questions/44134212/best-way-to-flatten-js-object-keys-and-values-to-a-single-depth-array
function flattenObject(ob) {
    var toReturn = {};

    for (var i in ob) {
        if (!ob.hasOwnProperty(i)) continue;

        if ((typeof ob[i]) == 'object' && ob[i] !== null) {
            var flatObject = flattenObject(ob[i]);
            for (var x in flatObject) {
                if (!flatObject.hasOwnProperty(x)) continue;

                toReturn[i + '__' + x] = flatObject[x];
            }
        } else {
            toReturn[i] = ob[i];
        }
    }
    return toReturn;
}

let parsed_message = msg.payload.parsed_message
let flattened_message = flattenObject(parsed_message)
delete msg.payload.parsed_message
msg.payload["flattened_message"] = flattened_message

return msg

