// Use an empty datapoint map if none has been received via MQTT yet to prevent
// the script from throwing errors.
let datapoint_map = global.get("datapoint_map") || {"sensor": {}, "actuator": {}}
let sensor_map = datapoint_map["sensor"]

let datapoint_msgs = []
let sensor_data_flat = msg.payload.flattened_message

// We could also iterate over all entries of sensor map, but this should be more
// efficient if we expect many messages with only some datapoints, as e.g. is
// the case for homematic.
for (let internal_sensor_id in sensor_data_flat){
    // Only process messages of datapoints which have been selected for sending
    // via MQTT in the central admin UI.
    if (internal_sensor_id in sensor_map){
        let datapoint_msg = {
            "payload": {
                "value": sensor_data_flat[internal_sensor_id],
                "timestamp": msg.payload.timestamp,
            },
            "topic": sensor_map[internal_sensor_id],
        }
        datapoint_msgs.push(datapoint_msg)
    }

}

return [datapoint_msgs]

