/*
Check that the datapoint map is structured as follows:
{"sensor": {}, "actuator": {}}
The empty objects above may contain the actuall mappings or not in reality, but
must exist. Else the filter nodes in the Main flow will fail and throw erros.
*/

// Based on an anwser here: 
// https://stackoverflow.com/questions/8511281/check-if-a-value-is-an-object-in-javascript
isObject = function(a) {
    return Object.prototype.toString.call(a) === "[object Object]"
}

let reject_datapoint_map_reason = ""

let datapoint_map = msg.payload

if ( ! isObject(datapoint_map) ) {
    reject_datapoint_map_reason = "Datapoint_map is no object."
} else if ( ! ("sensor" in datapoint_map) ) {
    reject_datapoint_map_reason = "No sensor key in datapoint_map."
} else if ( ! ("actuator" in datapoint_map) ) {
    reject_datapoint_map_reason = "No actuator key in datapoint_map."
} else if ( ! isObject(datapoint_map["sensor"]) ) {
    reject_datapoint_map_reason = "Sensor key in datapoint_map contains no object."
} else if ( ! isObject(datapoint_map["actuator"]) ) {
    reject_datapoint_map_reason = "Actuator key in datapoint_map contains no object."
}

if ( reject_datapoint_map_reason !== "" ){
    node.error("Rejecting datapoint_map with reason: " + reject_datapoint_map_reason)
} else {
    node.warn("got datapoint map", msg)
    return msg
}

/*
The structure of the incomming msg and the available datapoints object 
should be generally like:
"payload": {
	"sensor": {
		"Channel__P__value__0": 0.122,
        "Channel__P__unit__0": "kW",
	},
	"actuator": {
		"Channel__P__setpoint__0": 0.4,
	}
}
*/

// Check for each sensor/actuator datapoint if it is already known to 
// available_datapoints. Trigger sending an update if not so. Update the values 
// of the datapoints every time so next time we send available_datapoints it will
// carry examples of the most recent values.
let new_datapoints = false
let available_datapoints = global.get("available_datapoints") || {}
let available_datapoints_update = msg.payload

for (let datapoint_type of ["sensor", "actuator"]) {

    // Default to empty dict in case no datapoints of this type are known yet.
    let ad_per_type = available_datapoints[datapoint_type] || {}
    let ad_update_per_type = available_datapoints_update[datapoint_type] || {}

    for (let datapoint_id_update in ad_update_per_type) {

        if ( ! (datapoint_id_update in ad_per_type) ) {
            new_datapoints = true
        }

        // Store the latest datapoint value.
        ad_per_type[datapoint_id_update] = ad_update_per_type[datapoint_id_update]

    }

    // Store the type related sub_object back to the main object.
    available_datapoints[datapoint_type] = ad_per_type
}

// Store and return the updated available_datapoints.
global.set("available_datapoints", available_datapoints)

msg = {
    "payload": available_datapoints, 
    "new_datapoints": new_datapoints
}

return msg

// This is the unique channel that emitted the data, the device id and a 
// channel id. E.g. "OEQ1572982:2"
let channel = msg.payload.raw_message.channel
// The datapoint specifies which the information send on the channel.
// E.g. "HUMIDITY"
let datapoint = msg.payload.raw_message.datapoint
// Here is the actual value, e.g. 55
let value = msg.payload.raw_message.value

//let parsed_message = {}
//parsed_message[channel] = {}
//parsed_message[channel][datapoint] = value


let payload = {
    "parsed_message": {
        [channel]: {
            [datapoint]: value
        }
    }
}
return {"payload": payload}

// from https://stackoverflow.com/questions/44134212/best-way-to-flatten-js-object-keys-and-values-to-a-single-depth-array
function flattenObject(ob) {
    var toReturn = {};

    for (var i in ob) {
        if (!ob.hasOwnProperty(i)) continue;

        if ((typeof ob[i]) == 'object' && ob[i] !== null) {
            var flatObject = flattenObject(ob[i]);
            for (var x in flatObject) {
                if (!flatObject.hasOwnProperty(x)) continue;

                toReturn[i + '__' + x] = flatObject[x];
            }
        } else {
            toReturn[i] = ob[i];
        }
    }
    return toReturn;
}

let parsed_message = msg.payload.parsed_message
let flattened_message = flattenObject(parsed_message)
